package net.bither.image.exception;/** * http statusCode not 200 deal with access to the network(http),other don't * need */public class HttpException extends Exception {	private static final long serialVersionUID = 1L;	private int statusCode = -1;	public HttpException(String msg) {		super(msg);	}	public HttpException(Exception cause) {		super(cause);	}	public HttpException(String msg, int statusCode) {		super(msg);		this.statusCode = statusCode;	}	public HttpException(String msg, Exception cause) {		super(msg, cause);	}	public HttpException(String msg, Exception cause, int statusCode) {		super(msg, cause);		this.statusCode = statusCode;	}	public HttpException(int statusCode) {		this.statusCode = statusCode;	}	public int getStatusCode() {		return this.statusCode;	}}