package net.bither.image.http;import org.apache.http.HttpResponse;import org.apache.http.client.methods.HttpGet;public abstract class HttpGetResponse<T> extends BaseHttpResponse<T> {	public void handleHttpGet() throws Exception {		setHttpClient();		try {			HttpGet httpGet = new HttpGet(getUrl());			httpGet.setHeader("Accept", "application/json");			HttpResponse httpResponse = getHttpClient().execute(httpGet);			String response = getReponse(httpResponse);			setResult(response);		} catch (Exception e) {			throw e;		} finally {			getHttpClient().getConnectionManager().shutdown();		}	}}